import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useApp } from '../context/AppContext';
import { isAuthenticated } from '../services/authService';
import CoachingIQDrawer from '../components/CoachingIQDrawer';
import './RecruitingIQPage.css';

// Mock player database
const mockPlayers = [
  { id: '1', name: 'Marcus Johnson', position: 'PG', division: 'JUCO', school: 'South Plains CC', class: 'SO', region: 'South', state: 'TX', kpi: 78, nilReadiness: 6.5, confidence: 82, status: 'Unevaluated', lastUpdated: new Date().toISOString() },
  { id: '2', name: 'Kevin Williams', position: 'SG', division: 'NAIA', school: 'Southwestern CC', class: 'FR', region: 'Midwest', state: 'KS', kpi: 75, nilReadiness: 5.8, confidence: 79, status: 'Unevaluated', lastUpdated: new Date().toISOString() },
  { id: '3', name: 'Tyler Brown', position: 'SF', division: 'JUCO', school: 'Ranger College', class: 'SO', region: 'South', state: 'TX', kpi: 80, nilReadiness: 7.2, confidence: 85, status: 'Evaluated', final_kpi: 82, fit: 78, lastUpdated: new Date().toISOString() },
  { id: '4', name: 'James Davis', position: 'PF', division: 'NAIA', school: 'Bacone College', class: 'JR', region: 'South', state: 'OK', kpi: 76, nilReadiness: 6.0, confidence: 80, status: 'Unevaluated', lastUpdated: new Date().toISOString() },
  { id: '5', name: 'Chris Miller', position: 'C', division: 'JUCO', school: 'Odessa College', class: 'FR', region: 'South', state: 'TX', kpi: 73, nilReadiness: 5.5, confidence: 77, status: 'Unevaluated', lastUpdated: new Date().toISOString() },
  { id: '6', name: 'D. Smith', position: 'CG', division: 'NCAA D2', school: 'West Texas A&M', class: 'SR', region: 'South', state: 'TX', kpi: 81, nilReadiness: 7.8, confidence: 88, status: 'Evaluated', final_kpi: 84, fit: 82, archetype: 'Offensive Threat', lastUpdated: new Date().toISOString() },
];

const RecruitingIQPage = () => {
  const navigate = useNavigate();
  const { coachProfile, recruitingState, setRecruitingState, teamState, coachingBias } = useApp();

  const [filters, setFilters] = useState({
    search: '',
    division: ['JUCO', 'NAIA', 'Portal'], // Default view
    position: '',
    class: '',
    region: '',
    state: '',
    archetype: ''
  });

  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [detailTab, setDetailTab] = useState('profile'); // profile, evaluation, market, notes
  const [viewMode, setViewMode] = useState('list'); // list or board
  const [showAddProspectModal, setShowAddProspectModal] = useState(false);
  const [showDetailPanel, setShowDetailPanel] = useState(false);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const [showDrawer, setShowDrawer] = useState(false);
  const [newProspect, setNewProspect] = useState({
    name: '',
    position: '',
    division: '',
    school: '',
    class: ''
  });

  // Calculate roster composition
  const getRosterComposition = () => {
    if (!teamState?.roster || teamState.roster.length === 0) {
      return { PG: 0, CG: 0, Wing: 0, F: 0, Big: 0 };
    }

    const composition = { PG: 0, CG: 0, Wing: 0, F: 0, Big: 0 };
    teamState.roster.forEach(player => {
      if (player.position === 'PG') composition.PG++;
      else if (player.position === 'SG') composition.CG++;
      else if (player.position === 'SF' || player.position === 'SG') composition.Wing++;
      else if (player.position === 'PF' || player.position === 'SF') composition.F++;
      else if (player.position === 'C' || player.position === 'PF') composition.Big++;
    });

    return composition;
  };

  // Calculate summary metrics
  const calculateSummaryMetrics = () => {
    const activeRecruits = recruitingState?.activeRecruits || [];
    const priorityRecruits = activeRecruits.filter(r => r.isPriority);
    const committedRecruits = activeRecruits.filter(r => r.status === 'Committed');
    const committedPercent = activeRecruits.length > 0 
      ? Math.round((committedRecruits.length / activeRecruits.length) * 100) 
      : 0;

    // Calculate budget used
    const totalScholarshipCommitted = activeRecruits.reduce((sum, r) => sum + (r.scholarshipOffer || 0), 0);
    const totalNILCommitted = activeRecruits.reduce((sum, r) => sum + (r.nilOffer || 0), 0);
    const scholarshipCap = coachingBias?.scholarshipCap || 12;
    const nilPool = coachingBias?.nilPool || 50000;
    const scholarshipUsed = teamState?.roster?.reduce((sum, p) => {
      const slot = p.scholarship_slot || (p.scholarship ? parseFloat(p.scholarship) / (scholarshipCap * 10000) : 0);
      return sum + slot;
    }, 0) || 0;
    const nilUsed = teamState?.roster?.reduce((sum, p) => sum + (p.nil_value_suggestion || p.nil || 0), 0) || 0;

    const totalScholarshipUsed = scholarshipUsed + (totalScholarshipCommitted / (scholarshipCap * 10000));
    const totalNILUsed = nilUsed + totalNILCommitted;
    const budgetUsedPercent = scholarshipCap > 0 && nilPool > 0
      ? Math.round(((totalScholarshipUsed / scholarshipCap) * 0.5 + (totalNILUsed / nilPool) * 0.5) * 100)
      : 0;

    return {
      activeRecruits: activeRecruits.length,
      priorityRecruits: priorityRecruits.length,
      committedPercent,
      budgetUsedPercent
    };
  };

  const filteredPlayers = mockPlayers.filter(player => {
    if (filters.search) {
      const searchLower = filters.search.toLowerCase();
      const matchesName = player.name?.toLowerCase().includes(searchLower);
      const matchesSchool = player.school?.toLowerCase().includes(searchLower);
      const matchesCity = player.city?.toLowerCase().includes(searchLower);
      const matchesState = player.state?.toLowerCase().includes(searchLower);
      if (!matchesName && !matchesSchool && !matchesCity && !matchesState) return false;
    }
    if (filters.division && filters.division.length > 0 && !filters.division.includes(player.division)) return false;
    if (filters.position && player.position !== filters.position) return false;
    if (filters.class && player.class !== filters.class) return false;
    if (filters.region && player.region !== filters.region) return false;
    if (filters.state && player.state !== filters.state) return false;
    if (filters.archetype && player.archetype !== filters.archetype) return false;
    return true;
  });

  // Sort players
  const sortedPlayers = [...filteredPlayers].sort((a, b) => {
    if (!sortConfig.key) return 0;

    let aValue, bValue;
    switch (sortConfig.key) {
      case 'kpi':
        aValue = a.kpi || 0;
        bValue = b.kpi || 0;
        break;
      case 'nilReadiness':
        aValue = a.nilReadiness || 0;
        bValue = b.nilReadiness || 0;
        break;
      case 'confidence':
        aValue = a.confidence || 0;
        bValue = b.confidence || 0;
        break;
      case 'class':
        const classOrder = { 'FR': 1, 'SO': 2, 'JR': 3, 'SR': 4, 'HS FR': 1, 'HS SO': 2, 'HS JR': 3, 'HS SR': 4 };
        aValue = classOrder[a.class] || 0;
        bValue = classOrder[b.class] || 0;
        break;
      case 'region':
        aValue = a.region || '';
        bValue = b.region || '';
        break;
      case 'division':
        aValue = a.division || '';
        bValue = b.division || '';
        break;
      default:
        return 0;
    }

    if (sortConfig.direction === 'asc') {
      return aValue > bValue ? 1 : -1;
    } else {
      return aValue < bValue ? 1 : -1;
    }
  });

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const handleViewPlayer = (player) => {
    setSelectedPlayer(player);
    setShowDetailPanel(true);
  };

  const getDivisionColor = (division) => {
    if (division === 'JUCO') return '#D4AF37';
    if (division === 'NAIA') return '#4ECDC4';
    if (division?.includes('NCAA')) return 'linear-gradient(135deg, #C0C0C0, #808080)';
    return '#FFFFFF';
  };

  const getRegionFromState = (state) => {
    // Simple mapping - can be expanded
    const west = ['CA', 'OR', 'WA', 'NV', 'AZ', 'UT', 'CO', 'WY', 'MT', 'ID', 'NM', 'AK', 'HI'];
    const midwest = ['IL', 'IN', 'MI', 'OH', 'WI', 'IA', 'KS', 'MN', 'MO', 'NE', 'ND', 'SD'];
    const south = ['TX', 'FL', 'GA', 'NC', 'VA', 'TN', 'AL', 'LA', 'MS', 'AR', 'OK', 'KY', 'SC', 'WV'];
    const east = ['NY', 'PA', 'NJ', 'MA', 'CT', 'RI', 'VT', 'NH', 'ME', 'MD', 'DE', 'DC'];

    if (west.includes(state)) return 'West';
    if (midwest.includes(state)) return 'Midwest';
    if (south.includes(state)) return 'South';
    if (east.includes(state)) return 'East';
    return 'Unknown';
  };

  const rosterComposition = getRosterComposition();
  const summaryMetrics = calculateSummaryMetrics();

  const handleAddToBoard = (player) => {
    console.log('[LOGIC HOOK: handleAddToBoard] Adding player to recruiting board:', player.id);

    // Check if already on board
    const existingRecruit = recruitingState?.activeRecruits?.find(r => r.id === player.id);
    if (existingRecruit) {
      alert(`${player.name} is already on the Recruiting Board`);
      return;
    }

    const newRecruit = {
      ...player,
      status: 'Active',
      isPriority: false,
      scholarshipOffer: 0,
      nilOffer: 0,
      dateAdded: new Date().toISOString()
    };

    setRecruitingState(prev => ({
      ...prev,
      activeRecruits: [...(prev.activeRecruits || []), newRecruit]
    }));

    // Coach K™ prompt
    const activeCount = (recruitingState?.activeRecruits?.length || 0) + 1;
    if (window.addCoachKMessage) {
      window.addCoachKMessage({
        id: 'RIQ_PLAYER_ADDED',
        speaker: 'Coach K',
        message: `Player added to board · you now have ${activeCount} active prospects.`,
        type: 'success'
      });
    }

    alert(`✅ ${player.name} added to Recruiting Board`);
  };

  const handleEvaluate = (player) => {
    console.log('[LOGIC HOOK: handleEvaluate] Evaluating player:', player.id);
    navigate('/player-iq', { state: { playerData: player } });
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
    console.log('[LOGIC HOOK: handleFilterChange] Filter updated:', { [key]: value });
  };

  const handleAddProspect = () => {
    console.log('[LOGIC HOOK: handleAddProspect] Adding new prospect:', newProspect);
    const prospect = {
      id: Date.now().toString(),
      ...newProspect,
      kpi: Math.floor(Math.random() * 20) + 70,
      nilReadiness: Math.floor(Math.random() * 4) + 6,
      confidence: Math.floor(Math.random() * 10) + 75,
      status: 'Unevaluated'
    };
    handleAddToBoard(prospect);
    setShowAddProspectModal(false);
    setNewProspect({ name: '', position: '', division: '', school: '', class: '' });
  };

  useEffect(() => {
    if (!isAuthenticated() || !coachProfile) {
      navigate('/login');
      return;
    }

    // Listen for Coaching IQ drawer open event
    const handleOpenDrawer = () => {
      setShowDrawer(true);
    };

    window.addEventListener('openCoachingIQDrawer', handleOpenDrawer);

    return () => {
      window.removeEventListener('openCoachingIQDrawer', handleOpenDrawer);
    };
  }, [navigate, coachProfile]);

  if (!isAuthenticated() || !coachProfile) {
    return null;
  }

  const scholarshipCap = coachingBias?.scholarshipCap || 12;
  const scholarshipSlots = teamState?.roster?.reduce((sum, p) => {
    const slot = p.scholarship_slot || (p.scholarship ? parseFloat(p.scholarship) / (scholarshipCap * 10000) : 0);
    return sum + slot;
  }, 0) || 0;
  const nilUsed = teamState?.roster?.reduce((sum, p) => sum + (p.nil_value_suggestion || p.nil || 0), 0) || 0;
  const nilPool = coachingBias?.nilPool || 50000;
  const remainingSlots = scholarshipCap - scholarshipSlots;
  const remainingNIL = nilPool - nilUsed;

  return (
    <div className="recruiting-iq-page">
      {/* Header - Dual Strip */}
      <header className="recruiting-iq-header">
        {/* Main Bar (80px) */}
        <div className="header-main-bar">
          {/* Left Zone - Program Context + Roster Summary */}
          <div className="header-left">
            <h1 className="header-title">RECRUITING IQ™ DATABASE</h1>
            <p className="subtitle">National prospect feed · dynamic by level and region.</p>
            <div className="context-line">
              <span 
                className="context-field clickable" 
                onClick={() => navigate('/team-iq')}
                title="View Team IQ Roster"
              >
                Team: {coachProfile?.team || 'Not Set'}
              </span>
              <span className="context-divider">·</span>
              <span 
                className="context-field clickable"
                onClick={() => {
                  const event = new CustomEvent('openCoachingIQDrawer');
                  window.dispatchEvent(event);
                }}
                title="Open Coaching IQ Drawer"
              >
                System: {coachProfile?.offense || 'Not Set'} / {coachProfile?.defense || 'Not Set'}
              </span>
              <span className="context-divider">·</span>
              <span 
                className="context-field clickable"
                title={`Remaining slots ${remainingSlots.toFixed(1)} · Remaining NIL $${remainingNIL.toLocaleString()}`}
              >
                Scholarships: {scholarshipSlots.toFixed(1)} / {scholarshipCap}
              </span>
              <span className="context-divider">·</span>
              <span 
                className="context-field clickable"
                title={`Remaining slots ${remainingSlots.toFixed(1)} · Remaining NIL $${remainingNIL.toLocaleString()}`}
              >
                NIL Used: {Math.round((nilUsed / nilPool) * 100)}%
              </span>
            </div>
            {/* Roster Composition Summary */}
            <div className="roster-composition">
              <span className="comp-item">PG {rosterComposition.PG}</span>
              <span className="comp-divider">·</span>
              <span className="comp-item">CG {rosterComposition.CG}</span>
              <span className="comp-divider">·</span>
              <span className="comp-item">Wing {rosterComposition.Wing}</span>
              <span className="comp-divider">·</span>
              <span className="comp-item">F {rosterComposition.F}</span>
              <span className="comp-divider">·</span>
              <span className="comp-item">Big {rosterComposition.Big}</span>
            </div>
          </div>

          {/* Center Zone - Search / Filter Controls */}
          <div className="header-center">
            <div className="search-bar">
              <input
                type="text"
                placeholder="Search by name, school, city, state..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
                className="search-input"
              />
            </div>
            <div className="filter-controls">
              <select 
                value={filters.position} 
                onChange={(e) => handleFilterChange('position', e.target.value)}
                className="filter-select"
              >
                <option value="">All Positions</option>
                <option value="PG">PG</option>
                <option value="CG">CG</option>
                <option value="Wing">Wing</option>
                <option value="F">F</option>
                <option value="Big">Big</option>
              </select>
              <select 
                value={filters.class} 
                onChange={(e) => handleFilterChange('class', e.target.value)}
                className="filter-select"
              >
                <option value="">All Classes</option>
                <option value="HS FR">HS FR</option>
                <option value="HS SO">HS SO</option>
                <option value="HS JR">HS JR</option>
                <option value="HS SR">HS SR</option>
                <option value="FR">FR</option>
                <option value="SO">SO</option>
                <option value="JR">JR</option>
                <option value="SR">SR</option>
                <option value="GR">GR</option>
              </select>
            </div>
          </div>

          {/* Right Zone - Global Actions & Navigation */}
          <div className="header-right">
            <button 
              className="action-btn-icon" 
              onClick={() => {
                const event = new CustomEvent('openCoachingIQDrawer');
                window.dispatchEvent(event);
              }}
              title="Coaching IQ™"
            >
              <span className="icon">🧠</span>
              <span className="action-label">Coaching IQ™</span>
            </button>
            <button 
              className="action-btn-icon" 
              onClick={() => navigate('/team-iq')}
              title="Team IQ™"
            >
              <span className="icon">📊</span>
              <span className="action-label">Team IQ™</span>
            </button>
            <button 
              className="action-btn-icon" 
              onClick={() => setShowAddProspectModal(true)}
              title="Add Prospect"
            >
              <span className="icon">➕</span>
              <span className="action-label">Add Prospect</span>
            </button>
            <button 
              className="action-btn-icon" 
              onClick={() => navigate('/office')}
              title="Return to Office"
            >
              <span className="icon">🏠</span>
              <span className="action-label">Return to Office</span>
            </button>
          </div>
        </div>

        {/* Summary Line (40px) */}
        <div className="summary-line">
          <div className="summary-item">
            <span className="label">Active Recruits:</span>
            <span className="value">{summaryMetrics.activeRecruits}</span>
          </div>
          <div className="summary-item">
            <span className="label">Priority Recruits:</span>
            <span className="value priority" title="⭐ View Priority Board">
              ⭐ {summaryMetrics.priorityRecruits}
            </span>
          </div>
          <div className="summary-item">
            <span className="label">Committed %:</span>
            <span className="value">{summaryMetrics.committedPercent}%</span>
          </div>
          <div className="summary-item">
            <span className="label">Budget Used %:</span>
            <span className="value">{summaryMetrics.budgetUsedPercent}%</span>
          </div>
        </div>
      </header>

      {/* Extended Filter Bar */}
      <div className="filter-bar">
        <div className="filter-group">
          <label>Level / Division:</label>
          <div className="division-toggles">
            {['JUCO', 'NAIA', 'Portal', 'NCAA D1', 'NCAA D2', 'NCAA D3', 'Manual'].map(div => (
              <label key={div} className="toggle-option">
                <input
                  type="checkbox"
                  checked={filters.division.includes(div)}
                  onChange={(e) => {
                    if (e.target.checked) {
                      handleFilterChange('division', [...filters.division, div]);
                    } else {
                      handleFilterChange('division', filters.division.filter(d => d !== div));
                    }
                  }}
                />
                <span>{div}</span>
              </label>
            ))}
          </div>
        </div>
        <div className="filter-group">
          <label>Region:</label>
          <select 
            value={filters.region} 
            onChange={(e) => handleFilterChange('region', e.target.value)}
            className="filter-select"
          >
            <option value="">All Regions</option>
            <option value="West">West</option>
            <option value="Midwest">Midwest</option>
            <option value="South">South</option>
            <option value="East">East</option>
          </select>
        </div>
        <div className="filter-group">
          <label>State:</label>
          <select 
            value={filters.state} 
            onChange={(e) => handleFilterChange('state', e.target.value)}
            className="filter-select"
          >
            <option value="">All States</option>
            <option value="CA">California</option>
            <option value="TX">Texas</option>
            <option value="FL">Florida</option>
            <option value="NY">New York</option>
            {/* Add more states as needed */}
          </select>
        </div>
        <div className="filter-group">
          <label>Archetype Tag:</label>
          <select 
            value={filters.archetype} 
            onChange={(e) => handleFilterChange('archetype', e.target.value)}
            className="filter-select"
          >
            <option value="">All Archetypes</option>
            <option value="Offensive Threat">Offensive Threat</option>
            <option value="Defensive Anchor">Defensive Anchor</option>
            <option value="Playmaker">Playmaker</option>
            <option value="Stretch Big">Stretch Big</option>
            {/* Add more archetypes as needed */}
          </select>
        </div>
        <div className="view-toggle">
          <button 
            className={viewMode === 'list' ? 'active' : ''}
            onClick={() => setViewMode('list')}
          >
            List
          </button>
          <button 
            className={viewMode === 'board' ? 'active' : ''}
            onClick={() => setViewMode('board')}
          >
            Board
          </button>
        </div>
      </div>

      {/* Content */}
      <div className="recruiting-content">
        {viewMode === 'list' ? (
          <div className="list-view">
            <table className="player-table">
              <thead>
                <tr>
                  <th className="sortable" onClick={() => handleSort('name')}>
                    Name {sortConfig.key === 'name' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                  </th>
                  <th>POS</th>
                  <th className="sortable" onClick={() => handleSort('division')}>
                    DIVISION {sortConfig.key === 'division' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                  </th>
                  <th>SCHOOL</th>
                  <th className="sortable" onClick={() => handleSort('class')}>
                    CLASS {sortConfig.key === 'class' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                  </th>
                  <th className="sortable" onClick={() => handleSort('region')}>
                    REGION {sortConfig.key === 'region' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                  </th>
                  <th className="sortable" onClick={() => handleSort('kpi')}>
                    KPI (raw) {sortConfig.key === 'kpi' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                  </th>
                  <th className="sortable" onClick={() => handleSort('nilReadiness')}>
                    NIL READINESS {sortConfig.key === 'nilReadiness' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                  </th>
                  <th className="sortable" onClick={() => handleSort('confidence')}>
                    CONF % {sortConfig.key === 'confidence' && (sortConfig.direction === 'asc' ? '↑' : '↓')}
                  </th>
                  <th>STATUS</th>
                  <th>ACTIONS</th>
                </tr>
              </thead>
              <tbody>
                {sortedPlayers.map(player => {
                  const region = player.region || getRegionFromState(player.state);
                  const isSelected = selectedPlayer?.id === player.id;

                  return (
                    <tr 
                      key={player.id} 
                      className={isSelected ? 'selected' : ''}
                      onClick={() => handleViewPlayer(player)}
                    >
                      <td className="player-name clickable">{player.name}</td>
                      <td>{player.position}</td>
                      <td>
                        <span 
                          className="division-badge" 
                          style={{ 
                            borderColor: getDivisionColor(player.division),
                            color: getDivisionColor(player.division)
                          }}
                        >
                          {player.division}
                        </span>
                      </td>
                      <td>{player.school}</td>
                      <td>{player.class}</td>
                      <td>{region}</td>
                      <td className="kpi-value">{player.kpi}</td>
                      <td>{player.nilReadiness?.toFixed(1) || 'N/A'}/10</td>
                      <td>{player.confidence ? `${player.confidence}%` : '—'}</td>
                      <td>
                        <span className={`status-badge ${player.status?.toLowerCase()}`}>
                          {player.status || 'Unevaluated'}
                        </span>
                      </td>
                      <td>
                        <div className="action-buttons" onClick={(e) => e.stopPropagation()}>
                          <button 
                            className="action-icon-btn"
                            onClick={() => handleViewPlayer(player)}
                            title="View"
                          >
                            👁
                          </button>
                          <button 
                            className="action-icon-btn"
                            onClick={() => handleEvaluate(player)}
                            title="Evaluate"
                          >
                            🧠
                          </button>
                          <button 
                            className="action-icon-btn"
                            onClick={() => handleAddToBoard(player)}
                            title="Add to Board"
                          >
                            📋
                          </button>
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="board-view">
            {sortedPlayers.map(player => {
              const region = player.region || getRegionFromState(player.state);
              const divisionColor = getDivisionColor(player.division);

              return (
                <div 
                  key={player.id} 
                  className="player-card"
                  onClick={() => handleViewPlayer(player)}
                >
                  <div 
                    className="card-avatar"
                    style={{ background: typeof divisionColor === 'string' && divisionColor.includes('gradient') 
                      ? divisionColor 
                      : `linear-gradient(135deg, ${divisionColor}, ${divisionColor}dd)`
                    }}
                  >
                    {player.name.charAt(0)}
                  </div>
                  <h3>{player.name}</h3>
                  <p className="card-position">{player.position} · {player.division}</p>
                  <p className="card-school">{player.school} · {region}</p>
                  <div className="card-stats">
                    <div className="stat">
                      <span className="label">KPI:</span>
                      <span className="value">{player.kpi}</span>
                    </div>
                    <div className="stat">
                      <span className="label">NIL:</span>
                      <span className="value">{player.nilReadiness?.toFixed(1) || 'N/A'}</span>
                    </div>
                    <div className="stat">
                      <span className="label">Status:</span>
                      <span className={`status-badge ${player.status?.toLowerCase()}`}>
                        {player.status || 'Unevaluated'}
                      </span>
                    </div>
                  </div>
                  {/* Hover State - Expanded Info */}
                  <div className="card-hover-info">
                    {player.confidence && (
                      <div className="hover-stat">
                        <span className="label">Confidence:</span>
                        <span className="value">{player.confidence}%</span>
                      </div>
                    )}
                    {player.fit && (
                      <div className="hover-stat">
                        <span className="label">Fit %:</span>
                        <span className="value">{player.fit}%</span>
                      </div>
                    )}
                    {player.lastUpdated && (
                      <div className="hover-stat">
                        <span className="label">Updated:</span>
                        <span className="value">{new Date(player.lastUpdated).toLocaleDateString()}</span>
                      </div>
                    )}
                  </div>
                  <div className="card-actions" onClick={(e) => e.stopPropagation()}>
                    <button 
                      className="card-action-btn"
                      onClick={() => handleEvaluate(player)}
                    >
                      🧠 Evaluate
                    </button>
                    <button 
                      className="card-action-btn"
                      onClick={() => handleAddToBoard(player)}
                    >
                      📋 Add to Board
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      {/* Player Detail Panel (Expandable Sidebar) */}
      {showDetailPanel && selectedPlayer && (
        <div className="player-detail-sidebar slide-in">
          <div className="detail-sidebar-header">
            <div className="detail-header-info">
              <h2>{selectedPlayer.name}</h2>
              <div className="detail-header-meta">
                <span className="meta-item">{selectedPlayer.position}</span>
                <span className="meta-divider">·</span>
                <span className="meta-item">{selectedPlayer.division}</span>
                <span className="meta-divider">·</span>
                <span className="meta-item">{selectedPlayer.school}</span>
                <span className="meta-divider">·</span>
                <span className="meta-item">{selectedPlayer.region || getRegionFromState(selectedPlayer.state)}</span>
                {selectedPlayer.lastUpdated && (
                  <>
                    <span className="meta-divider">·</span>
                    <span className="meta-item">Updated: {new Date(selectedPlayer.lastUpdated).toLocaleDateString()}</span>
                  </>
                )}
              </div>
            </div>
            <button className="panel-close" onClick={() => {
              setShowDetailPanel(false);
              setSelectedPlayer(null);
            }}>×</button>
          </div>

          <div className="detail-sidebar-tabs">
            <button 
              className={detailTab === 'profile' ? 'active' : ''}
              onClick={() => setDetailTab('profile')}
            >
              📊 Profile
            </button>
            <button 
              className={detailTab === 'evaluation' ? 'active' : ''}
              onClick={() => setDetailTab('evaluation')}
            >
              🧠 Evaluation
            </button>
            <button 
              className={detailTab === 'market' ? 'active' : ''}
              onClick={() => setDetailTab('market')}
            >
              💰 Market
            </button>
            <button 
              className={detailTab === 'notes' ? 'active' : ''}
              onClick={() => setDetailTab('notes')}
            >
              🗂 Notes
            </button>
          </div>

          <div className="detail-sidebar-content">
            {detailTab === 'profile' && (
              <div className="detail-section">
                <h3>Public Stats</h3>
                <div className="metric-group">
                  <div className="metric-item">
                    <label>Position:</label>
                    <div className="metric-value">{selectedPlayer.position}</div>
                  </div>
                  <div className="metric-item">
                    <label>Division:</label>
                    <div className="metric-value">{selectedPlayer.division}</div>
                  </div>
                  <div className="metric-item">
                    <label>School:</label>
                    <div className="metric-value">{selectedPlayer.school}</div>
                  </div>
                  <div className="metric-item">
                    <label>Class:</label>
                    <div className="metric-value">{selectedPlayer.class}</div>
                  </div>
                  <div className="metric-item">
                    <label>Region:</label>
                    <div className="metric-value">{selectedPlayer.region || getRegionFromState(selectedPlayer.state)}</div>
                  </div>
                </div>

                <h3>Raw KPI</h3>
                <div className="metric-group">
                  <div className="metric-item">
                    <label>Baseline KPI:</label>
                    <div className="metric-value gold">{selectedPlayer.kpi}</div>
                  </div>
                </div>

                <h3>Archetype Snapshot</h3>
                <div className="archetype-tags">
                  {selectedPlayer.archetype ? (
                    <span className="archetype-tag">{selectedPlayer.archetype}</span>
                  ) : (
                    <span className="archetype-tag placeholder">Not evaluated</span>
                  )}
                </div>
              </div>
            )}

            {detailTab === 'evaluation' && (
              <div className="detail-section">
                {selectedPlayer.final_kpi || selectedPlayer.fit ? (
                  <>
                    <h3>Evaluation Metrics</h3>
                    <div className="metric-group">
                      <div className="metric-item">
                        <label>Final KPI:</label>
                        <div className="metric-value gold">{selectedPlayer.final_kpi || selectedPlayer.kpi}</div>
                      </div>
                      <div className="metric-item">
                        <label>Fit %:</label>
                        <div className="metric-value">{selectedPlayer.fit || 'N/A'}%</div>
                      </div>
                      <div className="metric-item">
                        <label>Confidence %:</label>
                        <div className="metric-value">{selectedPlayer.confidence || 'N/A'}%</div>
                      </div>
                    </div>

                    <h3>Archetype Tags</h3>
                    <div className="archetype-tags">
                      {selectedPlayer.archetype ? (
                        <span className="archetype-tag">{selectedPlayer.archetype}</span>
                      ) : (
                        <span className="archetype-tag placeholder">Not evaluated</span>
                      )}
                    </div>

                    <h3>Badges</h3>
                    <div className="badges-section">
                      {selectedPlayer.badges?.map((badge, i) => (
                        <span key={i} className="badge">{badge}</span>
                      ))}
                      {(!selectedPlayer.badges || selectedPlayer.badges.length === 0) && (
                        <span className="badge placeholder">No badges</span>
                      )}
                    </div>
                  </>
                ) : (
                  <div className="empty-evaluation">
                    <p>This player has not been evaluated yet.</p>
                    <button className="primary-btn" onClick={() => handleEvaluate(selectedPlayer)}>
                      Run Evaluation
                    </button>
                  </div>
                )}
              </div>
            )}

            {detailTab === 'market' && (
              <div className="detail-section">
                <h3>NIL Readiness</h3>
                <div className="metric-group">
                  <div className="metric-item">
                    <label>NIL Readiness (0-10):</label>
                    <div className="metric-value">{selectedPlayer.nilReadiness?.toFixed(1) || 'N/A'}/10</div>
                  </div>
                </div>

                <h3>Valuation Metrics</h3>
                <div className="metric-group">
                  <div className="metric-item">
                    <label>Projected NIL Value:</label>
                    <div className="metric-value gold">
                      ${selectedPlayer.nilValueSuggestion?.toLocaleString() || 'N/A'}
                    </div>
                  </div>
                </div>

                <h3>Social Reach</h3>
                <div className="metric-group">
                  {selectedPlayer.socialReach ? (
                    <>
                      <div className="metric-item">
                        <label>Followers:</label>
                        <div className="metric-value">{selectedPlayer.socialReach.followers?.toLocaleString() || 'N/A'}</div>
                      </div>
                      <div className="metric-item">
                        <label>Engagement Rate:</label>
                        <div className="metric-value">{selectedPlayer.socialReach.engagementRate || 'N/A'}%</div>
                      </div>
                    </>
                  ) : (
                    <p className="empty-note">Social data not synced</p>
                  )}
                </div>
              </div>
            )}

            {detailTab === 'notes' && (
              <div className="detail-section">
                <textarea 
                  className="notes-textarea"
                  placeholder="Add notes about this prospect..."
                  value={selectedPlayer.notes || ''}
                  onChange={(e) => {
                    setSelectedPlayer({ ...selectedPlayer, notes: e.target.value });
                  }}
                />
              </div>
            )}
          </div>

          <div className="detail-sidebar-actions">
            <button className="secondary-btn" onClick={() => handleEvaluate(selectedPlayer)}>
              🧠 Evaluate
            </button>
            <button className="primary-btn" onClick={() => {
              handleAddToBoard(selectedPlayer);
              setShowDetailPanel(false);
            }}>
              📋 Add to Board
            </button>
            <button className="danger-btn" onClick={() => {
              // Remove from feed
              setShowDetailPanel(false);
              setSelectedPlayer(null);
            }}>
              Remove
            </button>
          </div>
        </div>
      )}

      {/* Add Prospect Modal */}
      {showAddProspectModal && (
        <div className="modal-overlay" onClick={() => setShowAddProspectModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Add New Prospect</h2>
              <button onClick={() => setShowAddProspectModal(false)}>×</button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Name *</label>
                <input
                  type="text"
                  value={newProspect.name}
                  onChange={(e) => setNewProspect({ ...newProspect, name: e.target.value })}
                  placeholder="Player Name"
                />
              </div>
              <div className="form-group">
                <label>Position</label>
                <select
                  value={newProspect.position}
                  onChange={(e) => setNewProspect({ ...newProspect, position: e.target.value })}
                >
                  <option value="">Select Position</option>
                  <option value="PG">PG</option>
                  <option value="SG">SG</option>
                  <option value="SF">SF</option>
                  <option value="PF">PF</option>
                  <option value="C">C</option>
                </select>
              </div>
              <div className="form-group">
                <label>Division</label>
                <select
                  value={newProspect.division}
                  onChange={(e) => setNewProspect({ ...newProspect, division: e.target.value })}
                >
                  <option value="">Select Division</option>
                  <option value="NCAA D1">NCAA D1</option>
                  <option value="NCAA D2">NCAA D2</option>
                  <option value="NCAA D3">NCAA D3</option>
                  <option value="NAIA">NAIA</option>
                  <option value="NCCAA">NCCAA</option>
                  <option value="JUCO">JUCO</option>
                  <option value="USCAA">USCAA</option>
                </select>
              </div>
              <div className="form-group">
                <label>School</label>
                <input
                  type="text"
                  value={newProspect.school}
                  onChange={(e) => setNewProspect({ ...newProspect, school: e.target.value })}
                  placeholder="School Name"
                />
              </div>
              <div className="form-group">
                <label>Class</label>
                <select
                  value={newProspect.class}
                  onChange={(e) => setNewProspect({ ...newProspect, class: e.target.value })}
                >
                  <option value="">Select Class</option>
                  <option value="FR">Freshman</option>
                  <option value="SO">Sophomore</option>
                  <option value="JR">Junior</option>
                  <option value="SR">Senior</option>
                </select>
              </div>
            </div>
            <div className="modal-footer">
              <button className="secondary-btn" onClick={() => setShowAddProspectModal(false)}>
                Cancel
              </button>
              <button className="primary-btn" onClick={handleAddProspect} disabled={!newProspect.name}>
                Add Prospect
              </button>
            </div>
          </div>
        </div>
      )}

      <CoachingIQDrawer 
        isOpen={showDrawer} 
        onClose={() => setShowDrawer(false)} 
      />
    </div>
  );
};

export default RecruitingIQPage;
