              </div>

              {/* Right Column - Identity & Details */}
              <div className="scope-details">
                <div className="identity-card">
                  <div className="player-avatar-large">
                    {scopeResult.image ? (
                      <img src={scopeResult.image} alt={scopeResult.name} />
