                    <div className={`confidence-band ${evaluationResult.confidenceTier.toLowerCase()}`}>
                      {evaluationResult.confidenceLabel}
                    </div>
                    <div className={`progress-bar ${evaluationResult.confidence < 70 ? 'low' : ''}`}>
                      <div 
                        className="progress-fill" 
