              } else {
                impact = `+${delta} made 3s if â‰¥ 15 min`;
              }

              return {
                player: player.name || `${position} Player`,
                archetype: archetype,
                impact: impact,
                icon: archetype.includes('Sniper') || archetype.includes('3&D') ? 'ðŸŽ¯' : 
                      archetype.includes('Slasher') ? 'âš¡' : 
                      archetype.includes('Def') || archetype.includes('Anchor') ? 'ðŸ§±' : 'ðŸŽ¯'
              };
            });
          playerImpacts.push(...topPlayers);
        }

