          confidence: Math.round(confidence),
          teamKPI: teamKPI,
          playerImpacts: playerImpacts,
          coachKNarrative: narratives[Math.floor(Math.random() * narratives.length)],
          totalScore: ourScore + theirScore
        });
      } else {
